mapper.insert:

INSERT INTO tbl_cart(
    cart_id,p_id,amount,user_idx
) VALUES (
    seq_cart.NEXTVAL,#{p_id}, #{amount},#{user_idx}
)

mapper.update:

UPDATE tbl_cart    
SET amount = #{amount} 
WHERE
AND p_id = #{p_id}

mapper.select:

 SELECT COUNT(*)
 FROM tbl_cart
WHERE p_id = #{p_id}

mapper.update:

UPDATE tbl_cart 
SET amount = amount + #{amount} 
WHERE p_id = #{p_id}
