mapper.insert:

INSERT INTO tbl_cart(
    cart_id, product_id, amount
) VALUES (
    seq_cart.NEXTVAL, #{productId}, #{amount}
)

mapper.select:


SELECT NVL(SUM(product_price * amount), 0) money
FROM tbl_cart c, tbl_product p
WHERE c.product_id = p.product_id;

mapper.update:

UPDATE tbl_cart    
SET amount = #{amount} 
WHERE product_id = #{productId}

mapper.select:

SELECT COUNT(*)
FROM tbl_cart
WHERE product_id = #{productId}

mapper.update:

UPDATE tbl_cart 
SET amount = amount + #{amount} 
WHERE product_id = #{productId}
