mapper.select:

  select shop_name
	from category_shop
	where shop_idx = #{shop_idx}

mapper.insert:


			select SHOP_SEQ.nextval from dual


        INSERT INTO t_product(p_id,p_name,p_price,p_desc,p_url)
        VALUES (
            SHOP_SEQ.NEXTVAL
            , #{p_name}
            , #{p_price}
            , #{p_desc}
            , #{p_url}
        )

mapper.update:

UPDATE t_product
SET p_name = #{p_name}
    , p_price = #{p_price}
    , p_desc = #{p_desc}
    , p_url = #{p_url}
WHERE
    p_id = #{p_id}

mapper.select:

SELECT   p_url AS p_url
FROM t_product
WHERE p_id = #{p_id}
